sourcesContent:
import Book from './models/Book';
import cheerio from 'cheerio';
import axios from 'axios';
import prettylink from 'prettylink';

export default (io) => {
  io.on('connection', function (socket) {
    console.log('Connection success', socket.id);

    const loadBooks = async () => {
      const books = await Book.find();
      io.emit('server:loadBooks', books);
    };
    loadBooks();

    socket.on('client:sendUrl', async (data) => {
      console.log(data, 'START - ONLY URL');

      const bookURL = data.url;

      if (bookURL.includes('https://www.amazon.com.br/')) {
        const bookInfo = await loadBook(bookURL);

        if (bookInfo.category !== 'Livros') {
          socket.emit('server:bookInfoErrorMsg', {
            err: 'Este produto não é um livro ou se trata de um Kindle',
          });
        }

        if (bookInfo.category == 'Livros') {
          console.log(bookInfo, 'END - ALL INFOS');
          socket.emit('server:bookInfo', bookInfo);
        }
      }
    });

    socket.on('client:newBook', async (data) => {
      console.log(data);
      const selectedBook = await Book.findOne({
        title: data.title,
      });

      if (selectedBook) {
        socket.emit('server:bookInfoErrorMsg', { err: 'Este livro já foi inserido' });
      }

      if (!selectedBook) {
        const newBook = new Book(data);

        try {
          await newBook.save();
          socket.emit('server:bookSavedMsg', { msg: 'O livro foi salvo' });
          loadBooks();
        } catch (error) {
          socket.emit('server:bookInfoErrorMsg', { err: 'Livro inválido' });
        }
      }
    });

    socket.on('client:removeBook', async (id) => {
      await Book.findByIdAndDelete(id);

      loadBooks();
    });

    socket.on('client:editBook', async (data) => {
      await Book.findByIdAndUpdate(data.id, { tag: data.tag });

      loadBooks();
    });
  });
};

async function loadBook(url) {
  const bookInfo = {};

  bookInfo.url = await module.exports.shortURL(url);

  await axios(url).then((res) => {
    const body = res.data;

    var $ = cheerio.load(body);

    bookInfo.title = $('#productTitle').text().trim();

    bookInfo.cover = $('#imgBlkFront').attr('src');

    bookInfo.price = $('span.a-color-base .a-size-base.a-color-price').text().trim();

    bookInfo.category = $('a.a-link-normal.a-color-tertiary').first().text().trim();
  });

  return bookInfo;
}

async function shortURL(url) {
  const bitly = new prettylink.Bitly('a298dcbfe3e0604d73fa0dcc7e75870b7bc5d725');
  bitly.init('a298dcbfe3e0604d73fa0dcc7e75870b7bc5d725');

  const bookShortURL = await bitly
    .short(url)
    .then((result) => {
      return result.link;
    })
    .catch((err) => {
      console.log(err);
    });

  return bookShortURL;
}

