sourcesContent:
import mongoose from 'mongoose';

export const connectDB = async () => {
  mongoose.connect(process.env.MONGO_CONNECTION_URL, {}, (error) => {
    if (error) {
      console.log(error);
    }
    if (!error) {
      console.log('Banco conectado');
    }
  });
};

