sourcesContent:
import User from '../model/User';

export const generateUserID = async () => {
  // let userID = parseInt(Math.random() * 10000);

  let userID = 3000;
  userID = userID.toString();

  const selectedUser = await User.findOne({ userID: userID });
  console.log(selectedUser, 'Já existe um usuário');
};

